<?phpnamespace yqn\sdkmiddle;use yqn\sdkmiddle\action\We\App;/** * Class Middle * @package yqn\sdkmiddle */class Middle{    private static $instance;    private $rpc;    private $we_app, $we_menu, $we_users;    public function __construct($config)    {        if ($this->rpc == null) {            $this->rpc = new Rpc($config);        }    }    /**     * 静态亦是初始化类     * @param $config     * @return Middle     */    public static function Instance($config)    {        if (self::$instance == null) {            self::$instance = new static($config);        }        return self::$instance;    }    /**     * 初始化微信APP结构     * @return App 微信APP操作方法     */    public function We_App()    {        if ($this->we_app == null) {            $this->we_app = new App($this->rpc);        }        return $this->we_app;    }}