<?phpnamespace yqn\sdkmiddle;class RPC{    private $conn;    function __construct($config)    {        $this->conn = fsockopen($config["host"], $config["port"], $errno, $errstr, $config["time_out"]);        if (!$this->conn) {            return;        }        stream_set_timeout($this->conn, $config["time_out"]);    }    public function Call($method, $params)    {        if (!$this->conn) {            return false;        }        $err = fwrite($this->conn, json_encode(array(                'method' => $method,                'params' => array($params),                'id' => 0,            )) . "\n");        if ($err === false)            return false;        $line = fgets($this->conn);        if ($line === false) {            return NULL;        }        return json_decode($line, true);    }}