<?phpnamespace yqn\sdkmiddle\action;use yqn\sdkmiddle\Rpc;class ModelBase{    protected $Rpc;    protected $apiPath = [];    function __construct($rpc)    {        if ($rpc instanceof Rpc) {            $this->Rpc = $rpc;        }    }    /**     * 添加APP     * @param $data 包含字段[appid,title]     * @return array     */    public function Add($data)    {        return $this->Call("Add", $data);    }    /**     * 修改     * @param $data     * @return array     */    public function Edit($data)    {        return $this->Call("Edit", $data);    }    /**     * 删除     * @param $data     * @return array     */    public function Del($id, $deleted = false)    {        return $this->Call("Del", ["id" => $id, "deleted" => $deleted]);    }    /**     * 获取信息     * @param $id     * @param string $fields     * @return array     */    public function Get($id, $fields = "")    {        return $this->Call("Get", ["id" => $id, "fields" => $fields]);    }    /**     * 获取列表     * @param $data     * @return array     */    public function List($data)    {        return $this->Call("List", $data);    }    /**     * 分页列表     * @param $data     * @return array     */    public function Page($data)    {        return $this->Call("Page", $data);    }    /**     * 从RPC获取数据     * @param $method     * @param $data     * @return array     */    protected function Call($method, $data)    {        if (!isset($this->apiPath[$method])) {            return $this->Result("没有对应的方法");        }        return $this->Rpc->Call($this->apiPath[$method], $data);    }    /**     * 输出结果     * @param $msg 消息，字符串或数组     * @return array     */    protected function Result($msg)    {        if (is_string($msg)) {            return ["code" => 500, "msg" => $msg];        }        return $msg;    }}